import React, { useState } from "react";

function App() {

  setInterval(updateTime, 1000);

  let now = new Date().toLocaleTimeString();
  const [time, setTime] = useState(now);

  function updateTime() {
    const newTime = new Date().toLocaleTimeString();
    setTime(newTime);
  }


  const [count, setCount] = useState(0);
  const [isMousedOver1, setMouseOver1] = useState(false);


  function handleMouseOver1() {
    setMouseOver1(true);
  }

  function handleMouseOut1() {
    setMouseOver1(false);
  }
  const [isMousedOver2, setMouseOver2] = useState(false);


  function handleMouseOver2() {
    setMouseOver2(true);
  }

  function handleMouseOut2() {
    setMouseOver2(false);
  }


  function increase() {
    setCount(count + 1);
  }

  function decrease() {
    setCount(count - 1);
  }

  return (
    <div className="container">
      <h1 class="time">{time}</h1>
      <h1 class="number">{count}</h1>
      <button onClick={decrease}style={{ backgroundColor: isMousedOver1 ? "black" : "white" }}
        onMouseOver1={handleMouseOver1}
        onMouseOut1={handleMouseOut1}>-</button>
      <button onClick={increase}style={{ backgroundColor: isMousedOver2 ? "black" : "white" }}
        onMouseOver2={handleMouseOver2}
        onMouseOut2={handleMouseOut2}>+</button>
    </div>
  );
}

export default App;
